(function (w, d, s, l, i) {
    w[l] = w[l] || []; w[l].push({
        'gtm.start':
            new Date().getTime(), event: 'gtm.js'
    }); var f = d.getElementsByTagName(s)[0],
        j = d.createElement(s), dl = l != 'dataLayer' ? '&l=' + l : ''; j.async = true; j.src =
        'https://www.googletagmanager.com/gtm.js?id=' + i + dl; f.parentNode.insertBefore(j, f);
})(window, document, 'script', 'dataLayer', 'GTM-563F6SP');

if (typeof Storage !== "undefined") {
  try {
    var targetingStr = localStorage.getItem("bcDFPTargetingParams");
    if (targetingStr && window.pm_adverts && pm_adverts.setTargeting) {
      var params = JSON.parse(targetingStr);

      params.forEach(function (item) {
        if (!pm_adverts.setTargeting[item.key]) {
          pm_adverts.setTargeting[item.key] = [];
        }

        item.value.forEach(function (val) {
          if (!pm_adverts.setTargeting[item.key].includes(val)) {
            pm_adverts.setTargeting[item.key].push(val);
          }
        });
      });

      console.log("✅ BlueConic DFP targeting merged:", pm_adverts.setTargeting);
    }
  } catch (e) {
    console.warn("⚠️ BlueConic targeting parse error:", e);
  }
}

body.script:
// CUSTOM ALPINE COMPONENTS
//-----------------------------
document.addEventListener('alpine:init', () => {

    // MENU POSITIONING
    // Fixes mobile menu as you reach it - needed for when content sits above menu (like ads)
    //-----------------------------
    Alpine.data('menuposition', () => ({
        istop: false,
        fixed: false,
        menuTop: false,
        init() {
            const base = this;
            /// scrolling
            let scrollRunning = false;
            window.addEventListener('scroll', (e) => {
                if (!scrollRunning) {
                    window.requestAnimationFrame(() => {
                        base.onChange();
                        scrollRunning = false;
                    });
                    scrollRunning = true;
                }
            });
            //resizing
            let resizeRunning = false;
            window.addEventListener('resize', (e) => {
                if (!resizeRunning) {
                    window.requestAnimationFrame(() => {
                        base.onChange();
                        resizeRunning = false;
                    });
                    resizeRunning = true;
                }
            });
            this.onChange();
        },
        onChange() {
            if (window.innerWidth < 1024) {
                this.menuTop = this.$root.closest('header').getBoundingClientRect().top;
                if (this.menuTop <= 0) {
                    this.fixed = true;
                } else {
                    this.fixed = false;
                }
            }
        }
    }));


    // MOBILE MENU DROPDOWN
    //-----------------------------
    // Acts as a dropdown, but disables body scrolling when open
    // Keyboard navigable and auto-closes when tabbing off
    Alpine.data('mobilemenu', () => ({
        open: false,
        init() {
            const base = this;
            // Close menu when clicking an in-page link (anchor)
            const anchors = this.$root.getElementsByTagName('a');
            Array.from(anchors).forEach(function (el) {
                el.addEventListener('click', function (e) {
                    let elHash = this.hash;
                    base.close();
                    base.$nextTick(function () {
                        if (elHash) {
                            document.getElementById(elHash.replace('#', '')).scrollIntoView();
                        }
                    });
                });
            });
            // resizing
            let resizeRunning = false;
            window.addEventListener('resize', (e) => {
                if (!resizeRunning) {
                    window.requestAnimationFrame(() => {
                        base.onResize();
                        resizeRunning = false;
                    });
                    resizeRunning = true;
                }
            });
            this.onResize();
        },
        toggle() {
            this.open ? this.close() : (this.open = true, this.bodyscroll(true), (!this.fixed && window.scrollTo(0, document.getElementsByTagName('header')[0].offsetTop))); //this.fixed references parent (mainmenuposition) object property
        },
        close(focusElement) {
            this.open = false;
            this.bodyscroll(false);
            focusElement && focusElement.focus();
        },
        bodyscroll(scroll) {
            scroll ? (document.body.classList.add('overflow-hidden'), document.documentElement.classList.add('overflow-hidden')) : (document.body.classList.remove('overflow-hidden'), document.documentElement.classList.remove('overflow-hidden'));
            // Adding classes to the HTML element as well as body ensures scrolling is disables on iOS 15

        },
        onResize() {
            // Close menus when resizing screen past breakpoint
            const currentWidth = window.innerWidth,
                breakpoint = 1024;
            if (currentWidth >= breakpoint && this.windowSize <= breakpoint - 1 || currentWidth <= breakpoint - 1 && this.windowSize >= breakpoint) {
                open ? this.close() : '';
            }
            this.windowSize = innerWidth;
        }
    }));


    // SEARCH DROPDOWN
    //-----------------------------
    Alpine.data('searchdropdown', () => ({
        open: false,
        searchinput: false,
        toggle(focusElement) {
            this.open ? this.close() : this.open = true, this.$nextTick(() => { focusElement && focusElement.focus() });
        },
        close(focusElement) {
            this.open = false;
            this.$nextTick(() => { focusElement && focusElement.focus() })
        },
    }));


    // MENU DROPDOWN
    //-----------------------------
    Alpine.data('menudropdown', () => ({
        open: false,
        windowSize: 0,
        init() {
            let resizeRunning = false,
                base = this;
            window.addEventListener('resize', (e) => {
                if (!resizeRunning) {
                    window.requestAnimationFrame(() => {
                        base.onResize();
                        resizeRunning = false;
                    });
                    resizeRunning = true;
                }
            });
            this.onResize();
        },
        toggle() {
            this.open ? this.close() : this.open = true;
        },
        close(focusElement) {
            this.open = false;
            focusElement && focusElement.focus();
        },
        onResize() {
            // Close menus when resizing screen past breakpoint
            const currentWidth = window.innerWidth,
                breakpoint = 1024;
            if (currentWidth >= breakpoint && this.windowSize <= breakpoint - 1 || currentWidth <= breakpoint - 1 && this.windowSize >= breakpoint) {
                open ? this.close() : '';
            }
            this.windowSize = innerWidth;
        }
    }));


    // DROPDOWN
    //-----------------------------
    Alpine.data('dropdown', () => ({
        open: false,
        toggle(focusElement) {
            this.open ? this.close() : this.open = true;
            focusElement && focusElement.focus();
        },
        close(focusElement) {
            this.open = false;
            focusElement && focusElement.focus();
        },
    }));


    // REFERENCES PANEL
    //-----------------------------
    Alpine.data('references', () => ({
        refsopen: false,
        refItems: document.getElementsByClassName("reference-item"),
        triggerElement: '',
        hightlightClass: 'bg-yellow-50 rounded-sm p-2',
        init() {
            var refs = document.querySelectorAll('a[href^="#B"]');
            Array.from(refs).forEach(function (ref) {
                ref.setAttribute("x-on:click", 'toggleRefs($event)');
                ref.classList.add('ref-anchor');
            });
        },
        // Toggle References Panel
        toggleRefs(e) {
            this.refsopen = !this.refsopen;
            if (this.refsopen) {
                this.openRefs(e);
            } else {
                this.closeRefs();
            }
        },
        // Open References panel
        openRefs(e) {
            var base = this,
                targetA = e.target.closest('a');
            this.triggerElement = document.activeElement;
            this.refsopen = true;
            e.preventDefault();
            // If trigger is a ref anchor, hightlight relevant reference
            if (e.target.classList.contains('ref-anchor')) {
                var refLink = e.target.getAttribute("href"),
                    refElement = document.getElementById(refLink.replace("#", ""));
                this.$nextTick(function () {
                    base.highlightRef(refElement);
                });
            }
        },
        // Close References panel
        closeRefs() {
            var base = this;
            this.refsopen = false;
            this.$nextTick(function () {
                // Apply focus to last active element
                if (base.triggerElement != "" || base.triggerElement == undefined) {
                    base.triggerElement.focus();
                }
            });
            this.removeHighlight();
        },
        // highlightRef — highlights a reference
        // param: target (element obj) — the reference to highlight
        highlightRef(target) {
            var classes = this.hightlightClass.split(' '),
                refItem = target.querySelector(".reference-item");
            classes.forEach(function (item) {
                refItem.classList.add(item);
            });
        },
        // removeHighlight — removes hightlight classes from references
        removeHighlight() {
            var base = this;
            Array.from(this.refItems).forEach(function (item) {
                var classes = base.hightlightClass.split(' ');
                classes.forEach(function (i) {
                    item.classList.remove(i);
                });
            });
        }
    }));

});
